'use strict';

module.exports = {
	get: 'NOTIFICATION_GET',
	getParser: () => ({
		'V1 Alarm Type': 0,
		'Notification Type': 'Siren',
		Event: 8,
	}),
	report: 'NOTIFICATION_REPORT',
	reportParser: report => {
		if (report && report['Notification Type'] === 'Siren' && report.hasOwnProperty('Event (Parsed)')) {

			if (report['Event:'] === '1' ) {
				return true;
			}

			if (report['Event:'] === '0' ) {
				return false;
			}
		}
		return null;
	},
};
